import React, { useState } from 'react';
import axios from 'axios';

function Login() {
  const [email, setEmail] = useState(''); // Change from "username" to "email"
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = () => {
    axios
      .post('http://localhost:8000/login', { email, password }) // Send "email" instead of "username"
      .then(response => {
        if (response.data.error === false) { // Check for "error" property, not "success"
          setMessage('Login successful');
        } else {
          setMessage('Invalid credentials');
        }
      })
      .catch(error => {
        console.error(error);
        setMessage('An error occurred during login');
      });
  };

  return (
    <div>
      <h1>Login</h1>
      <input
        type="text"
        placeholder="Email"
        value={email}
        onChange={e => setEmail(e.target.value)} // Update state with email
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={e => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
      <p>{message}</p>
    </div>
  );
}

export default Login;
